router.get('/getImagesByUser', validateToken, async(req,res) => {
    try {
		const images = await models.Image.findAll({
			where: {
				UserId: req.user.id
			},
			include: [{ all: true }],
		});
		res.status(200).json({ data: images });
	} catch (error) {
		res.status(400).send(error.message);
	}
})

